using namespace mlir;
using namespace mlir::triton::NVIDIA;
namespace ttng = mlir::triton::nvidia_gpu;

namespace {
struct TritonGpuInitShareMemory
    : public triton::impl::ConvertTritonGPUInitShareMemoryBase<
          TritonGpuInitShareMemory> {
  using ConvertTritonGPUInitShareMemoryBase::
      ConvertTritonGPUInitShareMemoryBase;

  void getDependentDialects(DialectRegistry &registry) const override {
    registry.insert<triton::nvgpu::NVGPUDialect, LLVM::LLVMDialect,
                    NVVM::NVVMDialect>();
  }

  TritonGpuInitShareMemory(int32_t computeCapability)
      : ConvertTritonGPUInitShareMemoryBase({computeCapability}) {}

  TritonGpuInitShareMemory(int32_t computeCapability, int32_t ptxVersion)
      : ConvertTritonGPUInitShareMemoryBase({computeCapability, ptxVersion}) {}

  void runOnOperation() override {
    MLIRContext *context = &getContext();
    ModuleOp mod = getOperation();
    mlir::LowerToLLVMOptions option(context);
    TargetInfo targetInfo(computeCapability, ptxVersion);
    option.overrideIndexBitwidth(32);
    TritonGPUToLLVMTypeConverter typeConverter(context, option, targetInfo);
    // Allocate shared memory and set barrier
    ModuleAllocation allocation(mod);
    ModuleMembarAnalysis membarPass(&allocation);
    membarPass.run();
    initSharedMemory(typeConverter);
  }

private:
  void initSharedMemory(LLVMTypeConverter &typeConverter) {
    ModuleOp mod = getOperation();
    OpBuilder b(mod.getBodyRegion());
    auto ctx = mod.getContext();
    auto loc = mod.getLoc();
    auto elemTy = typeConverter.convertType(b.getIntegerType(8));
    // Set array size 0 and external linkage indicates that we use dynamic
    // shared allocation to allow a larger shared memory size for each kernel.
    //
    // Ask for 16B alignment on global_smem because that's the largest we should
    // ever need (4xi32).
    auto arrayTy = LLVM::LLVMArrayType::get(elemTy, 0);
    auto global = b.create<LLVM::GlobalOp>(
        loc, arrayTy, /*isConstant=*/false, LLVM::Linkage::External,
        "global_smem", /*value=*/Attribute(), /*alignment=*/16,
        // Add ROCm support.
        static_cast<unsigned>(NVVM::NVVMMemorySpace::kSharedMemorySpace));
  }
};
} // namespace
namespace mlir {
namespace triton {
std::unique_ptr<OperationPass<ModuleOp>> createTritonGpuInitShareMemoryPass() {
  return std::make_unique<TritonGpuInitShareMemory>();
}
} // namespace triton
} // namespace mlir
